$(document).ready(function() {
    // Function to handle click events on filter buttons
    $('.tabs a').on('click', function(e) {
        e.preventDefault();
        
        // Remove 'active' class from all filter buttons
        $('.tabs a').removeClass('active');
        // Add 'active' class to the clicked filter button
        $(this).addClass('active');
        
        // Get the data tag associated with the clicked filter button
        var tag = $(this).data('tag');
        
        // Hide all media elements
        $('.media').hide();
        
        // If the tag is 'all', show all media elements
        if (tag === 'all') {
            $('.media').show();
        } else {
            // Otherwise, show only the media elements with the matching data tag
            $('.media.' + tag).show();
        }
    });
});
